angular.module('starter.controllers',[]).controller('PetIndexCtrl',function($scope,PetService){$scope.pets=PetService.all();$scope.enableBackButton=false;}).controller('PetDetailCtrl',function($scope,$stateParams,PetService){$scope.pet=PetService.get($stateParams.petsId);$scope.leftButtons=[{type:'button-clear',content:'<i class="icon ion-navicon-round"></i>',tap:function(e){$scope.sideMenuController.toggleLeft();}}];}).directive('fadeBar',function($timeout){return{restrict:'E',template:'<div class="fade-bar"></div>',replace:true,link:function($scope,$element,$attr){$timeout(function(){$scope.$watch('sideMenuController.getOpenRatio()',function(ratio){$element[0].style.opacity=Math.abs(ratio);});});}};}).directive('imageRotator',function($timeout){return{restrict:'A',link:function($scope,$element,$attr){$timeout(function(){$element.j360()});}};});